import React ,{useEffect, useState} from 'react';

import '../styles/pages/OrphanagesMap.css'



import mapMarkerimg from '../images/map-marker.svg';
import {Link} from 'react-router-dom';
import {Map, TileLayer, Marker, Popup} from 'react-leaflet'

import {FiPlus, FiArrowRight} from 'react-icons/fi'
import mapIcon from '../utils/mapIcon';
import api from '../services/api';


interface Orphanage {
    id: number,
    latitude: number,
    longitude: number,
    name: string
}

function OrphanagesMap(){

    const [orphanages, setOrphanages] = useState([]);

    useEffect(() =>{
        api.get('orphanages').then(response =>{
            setOrphanages(response.data);
        });
    },[]);


    return(
        <div id='page-map'>
            <aside>
                <header>
                    <img src={mapMarkerimg} alt="Happy"/>

                    <h2>Escolha um orfanato no mapa</h2>
                    <p>Muitas crianças estão
                        esperando a sua visita :)
                    </p>
                </header>

                <footer>
                    <strong>Blumenau</strong>
                    <span>Santa Catarina</span>
                </footer>
            </aside>

            <Map
            center={[-26.9139822,-49.0716195]}
            zoom={15}
            style={{width: '100%', height: '100%'}}
            >          
                 <TileLayer
                    url='https://a.tile.openstreetmap.org/{z}/{x}/{y}.png'
                /> 

                {/* <TileLayer
                url={`https://api.mapbox.com/styles/v1/mapbox/light-v10/tiles/256/{z}/{x}/{y}@2x?acess_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}>

                </TileLayer> */}

               {orphanages.map(orphanage => {
                    <Marker 
                    icon={mapIcon}
                    position={[orphanage.latitude,orphanage.longitude]}>
                    
  
                    <Popup closeButton={false} minWidth={240} maxWidth={240} className='map-popup'>
                      lar da maria
                      <Link to='/orphanages/1'> <FiArrowRight size={20} color='#fff'/></Link>
                    </Popup>
                  </Marker>

               })}

               
            </Map>


            <Link to='/orphanages/create' className='create-orphanage'>
            <FiPlus size={32} color='#fff'/>
            </Link>

        </div>
    )
}

export default OrphanagesMap
